import React, { useEffect, useState } from 'react';
import {Link} from 'react-router-dom';

function UserLogin() {

  //validation
  const handleSubmit = () => {
    var vName = document.getElementById("vName").value;
    var vNameError = document.getElementById("vNameError");
    let error = false;

    if (vName.length === 0) {
      vNameError.classList.remove("d-none");
      error = true;
    } else {
      vNameError.classList.add("d-none");
    }

    if (error === false) {

      const fetchItems = async () => {
        try {
          const response = await fetch('/api/tags/addTagData', {
            method: 'POST',
            body: JSON.stringify({ vName: vName }),
            headers: {
              'Content-Type': 'application/json',
            },
          });
          const data = await response.json();
          if(data.status == 'success'){
            window.location.href = '/tag';
          }
        } catch (error) {
          console.error(error);
          // Handle the error, e.g., set an error state or display an error message
        }
      };
      fetchItems();
    }
  };
  return (  
    <div className="container mt-2">
      <form>
        <h2 className="text-primary">Login</h2>
        <div className="form-group">
          <label style={{textAlign: "left!important"}} >Email address</label>
          <input type="email" className="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" placeholder="Enter email" />
          <small id="emailHelp" className="form-text text-muted text-left">We'll never share your email with anyone else.</small>
        </div>
        <div className="form-group">
          <label>Password</label>
          <input type="password" className="form-control" id="exampleInputPassword1" placeholder="Password" />
        </div>
        <div className="form-group form-check text-left">
          <input type="checkbox" className="form-check-input" id="exampleCheck1" />
          <label className="form-check-label">Check me out</label>
        </div>
        <button type="button" onClick={() => handleSubmit()} className="btn btn-primary">Submit</button>
      </form>
    </div>
  );
}

export default UserLogin;
