import React, { useEffect, useState } from 'react';

function TagAdd() {
  //validation
  const handleSubmit = () => {
    const vName = document.getElementById("vName").value;
    console.log(vName);
    let error = false;

    if (vName.length === 0) {
      document.getElementById("vNameError").style.display = "block";
      error = true;
    } else {
      document.getElementById("vNameError").style.display = "none";
    }

    if (error === false) {
      alert('submit');
    }
  };
  return (
    <form>
      <div className="form-group row mt-3">
        <div className="col-lg-4">
          <label>Tag Name</label>  
        </div>
        <div className="col-lg-4">
          <input type="text" className="form-control" id="vName" placeholder="Enter Tag Name" />
          <div id="vNameError" className="text-danger d-none">Please Enter Tag Name</div>
        </div>
        <div className="col-lg-4">
        <button type="button" onClick={() => handleSubmit()} className="btn btn-primary">Submit</button>
        </div>
      </div>
    </form>
  );
}

export default TagAdd;
