import React, { useEffect, useState } from 'react';

function TagAdd() {
  //validation
  const handleSubmit = () => {
    const vName = document.getElementById("vName").value;
    const vNameError = document.getElementById("vNameError");
    let error = false;

    if (vName.length === 0) {
      vNameError.classList.remove("d-none");
      error = true;
    } else {
      vNameError.classList.add("d-none");
    }

    if (error === false) {
      const [items, setItems] = useState([]);

      const fetchItems = async () => {
        try {
          const response = await fetch('/api/tags/addTagData', {
            method: 'POST',
            headers: {
              'Content-Type': 'application/json',
            },
          });
          const data = await response.json();
          setItems(data.data);
        } catch (error) {
          console.error(error);
          // Handle the error, e.g., set an error state or display an error message
        }
      };
    }
  };
  return (
    <form>
      <div className="form-group row mt-3">
        <div className="col-lg-4">
          <label>Tag Name</label>  
        </div>
        <div className="col-lg-4">
          <input type="text" className="form-control" id="vName" placeholder="Enter Tag Name" />
          <div id="vNameError" className="text-danger text-left d-none">Please Enter Tag Name</div>
        </div>
        <div className="col-lg-4">
        <button type="button" onClick={() => handleSubmit()} className="btn btn-primary">Submit</button>
        </div>
      </div>
    </form>
  );
}

export default TagAdd;
