import React, {useEffect, useState} from 'react';
// import {Link} from 'react-router-dom';

function Tweet() {
    useEffect( () => {
        fetchItems();
    }, []);

    const [items, setItems] = useState([]);

    const fetchItems = async () => {
        const data = await fetch('/api/user/postanduser',{
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
        });
        const items = await data.json();
        console.log(items.data.vName);
        setItems(items.data);
    };

    return(
        <section>
            {            
            items.map(item => (
                <div className="container-fluid p-3 w-50">
                    <div className="card-deck">
                        <div className="card">
                            <div className="card-body p-1">
                                <h6 className="card-title">{item.vName}</h6>
                                {item.vCity? <p className="card-text"><i>{item.vCity}</i></p> : null}
                                {item.post.vName? <p className="card-text"><i>{item.post.vName}</i></p> : null}
                            </div>
                        </div>
                    </div>
                </div>
            ))
            }
        </section>
    );
}

export default Tweet;
