import React, { useEffect, useState } from 'react';
import {Link} from 'react-router-dom';

function UserLogin() {

  //validation
  const handleSubmit = () => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');

    const handleLogin = () => {
      // Perform API call or authentication logic here
      // If the authentication is successful, you can store the user's ID in local storage
      // In this example, we're using a hardcoded user ID for demonstration purposes

      // Example authentication logic:
      if (username === 'your_username' && password === 'your_password') {
        // Store the user ID in local storage
        localStorage.setItem('userId', 'your_user_id');

        // Do something to handle successful login (e.g., redirect or update state)
        alert('Login successful!');
      } else {
        // Do something to handle login failure (e.g., display error message or clear form inputs)
        alert('Login failed!');
      }
    };

    var vName = document.getElementById("vName").value;
    var vNameError = document.getElementById("vNameError");
    let error = false;

    if (vName.length === 0) {
      vNameError.classList.remove("d-none");
      error = true;
    } else {
      vNameError.classList.add("d-none");
    }

    if (error === false) {

      const fetchItems = async () => {
        try {
          const response = await fetch('/api/tags/addTagData', {
            method: 'POST',
            body: JSON.stringify({ vName: vName }),
            headers: {
              'Content-Type': 'application/json',
            },
          });
          const data = await response.json();
          if(data.status == 'success'){
            window.location.href = '/tag';
          }
        } catch (error) {
          console.error(error);
          // Handle the error, e.g., set an error state or display an error message
        }
      };
      fetchItems();
    }
  };
  return (  
    <div className="container mt-2">
      <form>
        <h2 className="text-primary">Login</h2>
        <div className="form-group">
          <p className="text-left" >Email address</p>
          <input type="email" className="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" onChange={(event) => setUsername(event.target.value)} placeholder="Enter email" />
          <small id="emailHelp" className="form-text text-muted text-left">We'll never share your email with anyone else.</small>
        </div>
        <div className="form-group">
          <p className="text-left" >Password</p>
          <input type="password" className="form-control" id="exampleInputPassword1" placeholder="Password" onChange={(event) => setPassword(event.target.value)} />
        </div>
        <button type="button" onClick={() => handleSubmit()} className="btn btn-primary">Submit</button>
      </form>
    </div>
  );
}

export default UserLogin;
