import React, { useEffect, useState } from 'react';
import {Link} from 'react-router-dom';

function Tag() {
  useEffect(() => {
    fetchItems();
  }, []);

  const handleEdit = (id) => {
    // Handle edit logic using the id
    console.log(`Edit button clicked for ID: ${id}`);
  };

  const handleDelete = (id) => {
    // Handle delete logic using the id
    console.log(`Delete button clicked for ID: ${id}`);
  };

  const [items, setItems] = useState([]);

  const fetchItems = async () => {
    try {
      const response = await fetch('/api/tags/tagData', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
      });
      const data = await response.json();
      setItems(data.data);
    } catch (error) {
      console.error(error);
      // Handle the error, e.g., set an error state or display an error message
    }
  };

  return (
    <section className="row">
      <table className="table">
        <thead>
          <tr>
            <th scope="col">#</th>
            <th scope="col">Name</th>
            <th scope="col">Action</th>
            <Link to="/tag/add" className="btn btn-primary">
              Add
            </Link>
          </tr>
        </thead>
        <tbody>
          {items.map((item, index) => (
            <tr key={index}>
              <th scope="row">{index}</th>
              <td>{item.vName}</td>
              <td>
                <button type="button" className="btn btn-primary" onClick={() => handleEdit(item.iTagId)}>
                  Edit
                </button>
                <button type="button" className="btn btn-danger" onClick={() => handleDelete(item.iTagId)}>
                  Delete
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </section>
  );
}

export default Tag;
